// Get the content from the previous node
const content = items[0].json.content;

// Split the content into sections based on newlines followed by additional newlines
const sections = content.split(/\n{2,}/);

// Create a mapping for content categorization
const categorizedSections = sections.map((section, index) => {
    // Trim leading and trailing whitespace
    const trimmedSection = section.trim();

    // Initialize the category as "Text"
    let category = "Text";

    // Determine the category based on content patterns
    if (trimmedSection.startsWith("# ")) {
        category = "Header"; // Header identified by a single #
    } else if (trimmedSection.startsWith("## ")) {
        category = "Named Range"; // Named Range identified by ##
    } else if (trimmedSection.startsWith("### ")) {
        category = "Subheader"; // Subheader identified by ###
    } else if (trimmedSection.startsWith("- ") || trimmedSection.startsWith("* ")) {
        category = "Paragraph Bullets"; // Bulleted list identified by - or *
    } else if (trimmedSection.includes("Table:")) {
        category = "Table"; // Table identified by a specific prefix
    } else if (trimmedSection.includes("Page Break")) {
        category = "Page Break"; // Page Break keyword
    } else if (trimmedSection.includes("Footer")) {
        category = "Footer"; // Footer keyword
    } else if (trimmedSection.includes("Positioned Object")) {
        category = "Positioned Object"; // Positioned Object keyword
    } else if (trimmedSection.includes("Table Column")) {
        category = "Table Column"; // Table Column keyword
    } else if (trimmedSection.includes("Table Row")) {
        category = "Table Row"; // Table Row keyword
    }

    // Append two new lines before and one new line after headings and subheadings (except the first heading)
    let sectionContent = trimmedSection;
    if ((category === "Header" || category === "Named Range" || category === "Subheader") && index !== 0) {
        sectionContent = `\n\n${trimmedSection}\n`; // Two new lines before and one after
    } else {
        sectionContent += '\n'; // One new line after for other content
    }

    return {
        json: {
            category: category,
            sectionContent: sectionContent // Updated section content with new lines
        }
    };
});

// Return categorized sections
return categorizedSections;

