function formatHtmlToText(html) {
    // Basic replacements for HTML tags
    html = html.replace(/<h1>(.*?)<\/h1>/g, '# $1\n\n');  // Convert H1
    html = html.replace(/<h2>(.*?)<\/h2>/g, '## $1\n\n');  // Convert H2
    html = html.replace(/<h3>(.*?)<\/h3>/g, '### $1\n\n');  // Convert H3
    html = html.replace(/<p>(.*?)<\/p>/g, '$1\n\n');         // Convert paragraphs
    html = html.replace(/<li>(.*?)<\/li>/g, '- $1\n');       // Convert list items
    html = html.replace(/<ul>(.*?)<\/ul>/g, '');             // Remove <ul> tags
    html = html.replace(/<a href="(.*?)">(.*?)<\/a>/g, '[$2]($1)'); // Convert links

    // Remove any remaining HTML tags
    html = html.replace(/<[^>]+>/g, ''); 

    return html.trim();
}

