// Create an array to hold the rearranged items
const rearrangedItems = [];

// Loop over input items and push each message into the rearrangedItems array
for (const item of $input.all()) {
  rearrangedItems[item.json.idx] = {
    json: {
      message: item.json.message + '\n\n' // Add two new lines at the end of each message
    }
  };
}

// Return the rearranged items
return rearrangedItems.filter(Boolean); // Filter out any undefined entries
